package com.larryyu.valorantui.di

import android.os.Build
import androidx.annotation.RequiresApi
import com.larryyu.valorantui.domain.utils.RetrofitConstants
import io.ktor.client.HttpClient
import io.ktor.client.engine.okhttp.OkHttp
import io.ktor.client.plugins.contentnegotiation.ContentNegotiation
import io.ktor.serialization.kotlinx.json.json
import kotlinx.serialization.json.Json
import java.time.Duration

@RequiresApi(Build.VERSION_CODES.O)
object NetworkModule {

    fun provideKtorClient(): HttpClient {
        return HttpClient(OkHttp) {
            install(ContentNegotiation) {
                json(Json {
                    ignoreUnknownKeys = true
                    prettyPrint = true
                    isLenient = true
                })
            }
            engine {
                config {
                    connectTimeout(Duration.ofSeconds(RetrofitConstants.TIME_OUT.toLong()))
                    readTimeout(Duration.ofSeconds(RetrofitConstants.TIME_OUT.toLong()))
                    writeTimeout(Duration.ofSeconds(RetrofitConstants.TIME_OUT.toLong()))
                }
            }
        }
    }
}

